INSERT INTO `boss` VALUES ('83', '描述：一、需求描述：\n类别：利用序列生成网络进行诗歌生成\n进度：网上有已经利用seqgan生成诗歌的代码\n功能：在原有seqgan（序列生成对抗网络）的基础上进行改进，使得最终生成的诗歌一方面要满足诗歌的平仄规则的要求；其次，原有seqgan中采用的是蒙特卡洛树搜索算法，具有一定的随机性，改进的另一个点要设法降低它的随机性，提高效率；最后，原本生成的诗歌最终用BLEU（机器生成与人工创作之间的相似度）这个指标进行评测的，改进后，BLEU指标要明显高于原本的数值。总之，使得机器生成的诗歌要尽可能的接近人工创', '123', '2天', '1000元', '500', '2019-09-19', '基于seqgan网络，利用pytorch生成诗歌', '0', 'http://thirdwx.qlogo.cn/mmopen/vi_32/aNUSJkYYUeGicfC6YbezJG7mZK9pTNTwJsRFI6iasWYvdVs6UpnW60SIdRrcicmr4lIeelEVia83pw1Lgpj9UwXtkQ/132', null, '0');
